package org.bitcoins.core.protocol.psbt

import org.bitcoins.core.util.BitcoinSUnitTest
import scodec.bits.HexStringSyntax

/**
  * All test vectors in this suite taken from otficial
  * [[https://github.com/bitcoin/bips/blob/master/bip-0174.mediawiki BIP174]]
  * test vectors.
  *
  * @see [[https://github.com/bitcoin/bips/blob/master/bip-0174.mediawiki BIP174]]
  */
class PsbtTest extends BitcoinSUnitTest {

  behavior of "Psbt"

  it must "deserialize a PSBT with one P2PKH input and no outputs" in {
    val bytes =
      hex""
    // base64: 

    val psbt = Psbt.fromBytes(bytes)
    assert(psbt.inputs.length == 1)
    assert(psbt.outputs.isEmpty)
  }

  // First input is signed and finalized
  it must "deserialize a PSBT with one P2PKH input and one P2SH-P2WPKH input and no outputs" in {
    val bytes =
      hex""
    val psbt = Psbt.fromBytes(bytes)
    assert(psbt.inputs.length == 2)
    assert(psbt.outputs.isEmpty)
  }
}
